import { InputContainer, IconContainer, InputText, ErrorText, ViewPassword} from './styles';
import {IInput} from './type';
import { Controller } from "react-hook-form" 

const Input = ({leftIcon, name, control, errorMessage, viewPasswordIcon, isRequired = false,...rest}: IInput) => {
  return (
    <>
      <InputContainer>
        {leftIcon ? (<IconContainer>{leftIcon}</IconContainer>): null }
        {viewPasswordIcon ? (<ViewPassword>{viewPasswordIcon}</ViewPassword>): null }

        <Controller
          name={name}
          control={control}
          rules={{required:  true}}
          render={({ field }) => (  <InputText {...field} {...rest} /> )}
        />

      </InputContainer>
      {errorMessage ? <ErrorText>{errorMessage}</ErrorText> : null}
    </>
  )
}

export {Input};
