import React, { useState } from 'react';
import { InputContainer, IconContainer, InputText, ErrorText, ViewPassword} from './styles';
import {IInput} from './type';
import { Controller } from "react-hook-form" 

const Input = ({leftIcon, name, control, errorMessage, viewPasswordIcon, isRequired = false, type = 'text', customStyle, classname, ...rest}: IInput) => {

  // Estado para controlar o tipo do input (password ou text)
  const [inputType, setInputType] = useState(type);

  // Função para alternar o tipo de input
  const togglePasswordVisibility = () => {
    console.log('foi')
    setInputType(prevType => prevType === 'password' ? 'text' : 'password');
  };

  return (
    <>
      <InputContainer  style={customStyle} className={classname}>
        {leftIcon ? (<IconContainer>{leftIcon}</IconContainer>): null }
        {viewPasswordIcon ? (<ViewPassword onClick={togglePasswordVisibility} >{viewPasswordIcon}</ViewPassword>): null }

        <Controller
          name={name}
          control={control}
          rules={{ required: isRequired }}
          render={({ field }) => (
            type === 'checkbox' ? (
              // Renderização do checkbox
              <input {...field} type="checkbox" {...rest} />
            ) : (
              // Renderização padrão
              <InputText {...field} type={inputType} {...rest} />
            )
          )}
        />

        {viewPasswordIcon && type === 'password' && (
          <ViewPassword onClick={togglePasswordVisibility}>
            {viewPasswordIcon}
          </ViewPassword>
        )}
      </InputContainer>

      {errorMessage && <ErrorText>{errorMessage}</ErrorText>}
    </>
  )
}

export {Input};
