import React, { useState } from "react";
import { useForm, FormProvider } from "react-hook-form";
import {Step1, Step2, Step3, Step4} from "../../components/cadastroSteps"
import { CadastrarContainer, FormContainer, FormContent } from './styles';




const ProgressBar = ({ step }: { step: number }) => (
  <div className="progress-bar">
    {[1, 2, 3, 4].map((num) => (
      <div key={num} className={num <= step ? "active" : ""}>
        {num}
      </div>
    ))}
  </div>
);

// No MultiStepForm, adicione:



const Cadastrar = () => {
  const methods = useForm(); // Inicializa o react-hook-form
  const { handleSubmit } = methods; // Para capturar o evento de submit

  const [step, setStep] = useState(1);

  const nextStep = () => setStep((prev) => prev + 1);
  const prevStep = () => setStep((prev) => prev - 1);

  const onSubmit = (data: any) => {
    console.log("Formulário submetido:", data);
    // Aqui você pode enviar os dados para o backend
  };



  return (
    <CadastrarContainer>
    <header>
      <h1 className="logo">CondoFacil</h1>
      <a href="/login">Já possui uma conta?</a>
    </header>
    <ProgressBar step={step} />
    <FormContainer>
      <FormContent>
        <ProgressBar step={step} />
        <FormProvider {...methods}>
          <form onSubmit={handleSubmit(onSubmit)}>
            <div>
              {step === 1 && <Step1 nextStep={nextStep} />}
              {step === 2 && <Step2 nextStep={nextStep} prevStep={prevStep} />}
              {step === 3 && <Step3 nextStep={nextStep} prevStep={prevStep} />}
              {step === 4 && <Step4 prevStep={prevStep} />}


            </div>
          </form>
        </FormProvider>
      </FormContent>
    </FormContainer>

    </CadastrarContainer>

  );
};

export { Cadastrar };
