import styled, {css} from 'styled-components';

export const InputContainer = styled.div<{ isCheckbox?: boolean }>`
  width: 100%;
  height: 48px;
  border: 1px solid #000;
  display: flex;
  align-items: center;
  margin: 16px auto;
  position: relative;

  ${({ isCheckbox }) =>
    isCheckbox &&
    css`

       background-color: blue;
    `}
`;

export const IconContainer = styled.div`
    margin-left: 10px;
    width: 24px;
    height: 24px;
    svg{
        width: 100%;
        height: 100%;
    }

`;
export const InputText = styled.input`
    background: transparent;
    color: #000;
    border: 0;
    width: 100%;
    height: 48px;
    outline: none;
    margin-left: 10px;
    font-size: 16px;
`;
export const ViewPassword = styled.div`
    width: 24px;
    height: 24px;
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translate(0, -50%);
    display: flex;
    align-items: center;
    justify-content: center;

    svg{
        width: 100%;
        height: 100%;
    }

    &:hover{
        cursor: pointer
    }

`;
export const ErrorText = styled.p`
    color: #FF0000;
    font-size: 12px;
    margin-bottom: 5px;
`
